import React, { useState } from "react";

const Counter = () => {
  //0에서 출발
  //1씩 증가하고
  //1씩 감소하는

  const [count, setCount] = useState(0);
  //배열 반환 [상태의 값,상태변화 함수= = useState(초깃값)

  const onIncrease = () => {
    setCount(count + 1);
  };

  return (
    <div>
      <h2>{count}</h2>
      <button>+</button>
      <button>-</button>
    </div>
  );
};

export default Counter;
