const DiaryItem = ({
  onRemove,
  id,
  author,
  content,
  emotion,
  created_date,
}) => {
  const handelRemoce = () => {
    if (window.confirm(`${id}번째 일기를 정말 삭제하시겠습니까?`)) {
      onRemove(id);
    }
  };

  return (
    <div className="DiaryItem" key={id}>
      <div className="info">
        <span>
          작성자 : {author} | 감정점수 : {emotion}
        </span>
        <br />
        <span className="date">
          {new Date(created_date).toLocaleDateString()}
        </span>
      </div>
      <br />
      <div className="content">{content}</div>
      <button>삭제하기</button>
      <button
        onClick={() => {
          if (window.confirm(`${id}번째 일기를 정말 삭제하시겠습니까?`)) {
            onRemove(id);
          }
        }}
      >
        수정하기
      </button>
    </div>
  );
};

export default DiaryItem;
